import './App.css';
import { useState } from 'react';

function App() {
  const [bmi, setBmi] = useState(0);
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");

  const squared = (n) => {
    return n*n;
  }

  const onHeightChange = (event) => {
    setHeight(event.target.value);
    if(height && weight) {
      setBmi(weight/squared(height))
    }
  }

  const onWeightChange = (event) => {
    setWeight(event.target.value);
    if(height && weight) {
      setBmi(weight/squared(height))
    }
  }

  return (
    <div className="App">
      <form>
        <input id='form-height' type="number" required step="0.01" min="0.10" max="3" placeholder='height (m)' value={height} onChange={onHeightChange}></input>
        <input id='form-weight' type="number" required step="0.01" min="2" max="700" placeholder='weight (kg)' value={weight} onChange={onWeightChange}></input>
        <button type="submit" onClick={onClickSubmit}>Calculate</button>
        <label id='bmi-value'>{bmi}</label>
      </form>
    </div>
  );

}
export default App;
