import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    height: '',
    weight: '',
    bmi: 0,
    isError: false
};

export const bmiSlice = createSlice({
    name: 'bmi',
    initialState,
    reducers: {
        setHeight: (state, action) => {
            state.height = action.payload;
        },
        setWeight: (state, action) => {
            state.weight = action.payload;
        },
        setBmi: (state, action) => {
            state.bmi = action.payload;
        },
        toggleIsError: (state) => {
            state.isError = !state.isError;
        }
    }
});

export const { 
    setHeight,
    setWeight,
    setBmi 
} = bmiSlice.actions


export const selectHeight = (state) => {
    return state.bmi.height;
}

export const selectWeight = (state) => {
    return state.bmi.weight;
}

export const selectBmi = (state) => {
    return state.bmi.bmi;
}


export default bmiSlice.reducer
