import './App.css';
import { useState } from 'react';

function App() {
  const [bmi, setBmi] = useState(0);

  const squared = (n) => {
    return n*n;
  }

  const onClickSubmit = (event) => {
    const height = document.getElementById("form-height").value;
    const weight = document.getElementById("form-weight").value;

    setBmi(weight/squared(height))
  }

  return (
    <div className="App">
      <form>
        <input id='form-height' type="number" required step="0.01" min="0.10" max="3" placeholder='height (m)'></input>
        <input id='form-weight' type="number" required step="0,01" min="2" max="700" maxplaceholder='weight (kg)'></input>
        <button type="submit" onClick={onClickSubmit}>Calculate</button>
        <label id='bmi-value'>{bmi}</label>
      </form>
    </div>
  );

}
export default App;
