import { configureStore } from "@reduxjs/toolkit";
import { persistStore, persistReducer } from "redux-persist";
import bmiReducer from "./slices/bmiSlice";

const store =  configureStore({
    reducer: {
        bmi: bmiReducer
    }
});

export default store;
