import styled from 'styled-components'
import { CATEGORIES } from '../../bmiSlice'

export const BMIContainer = styled.div`
    text-align: center;
    margin-top: 18vh;
`

export const BMIValue = styled.label`
    position: flex;
    color: ${({ category }) => {
                    switch(category){
                        case CATEGORIES.UNDERWEIGHT:
                            return `rgba(0,212,255,1)`
                        default:
                            return `rgba(0,0,0,0)`
                        };
                    }
            };
    font-size: 190px;
`
