import React, {useEffect, useState} from "react";
import {squared} from "../../utils/utils"
import { useFormContext } from "../form-context/form-context.component";
import {BMIContainer, BMIValue} from "./bmi-display.styles.jsx"


const BMIDisplay = () => {
    const [bmi, setBmi] = useState(0);
    const {height, weight} = useFormContext();

    useEffect(()=>{
        if(height && weight) {
            setBmi((weight/squared(height)).toFixed(2))
        }else {
            setBmi(0)
        }
    }, [height, weight])

    return (
            <BMIContainer>
                {((height && (height <= 0 || height >= 3)) || (weight && (weight <= 0 || weight >= 700))) ? 
                    (<BMIValue>0</BMIValue>): <BMIValue>{bmi}</BMIValue>}
            </BMIContainer>
    )
}

export default BMIDisplay;
