import { CATEGORIES } from '../redux/slices/bmiSlice'

export const squared = (n) => {
    return n*n;
}

export const handleTextColorByCategory = (category) => {
    switch(category){
        case CATEGORIES.UNDERWEIGHT:
            return `#ff00fb`
        case CATEGORIES.HEALTHY:
            return `#6bde63`
        case CATEGORIES.OVERWEIGHT:
            return `#ff8200`
        case CATEGORIES.OBESE:
            return `#ff0000`
        default:
            return `rgba(0,212,255,1)`
    };
}

export const handleBackgroudColorByCategory = (category) => {
    switch(category){
        case CATEGORIES.UNDERWEIGHT:
            return `linear-gradient(90deg, rgba(0,0,0,1) 0%, rgba(189,112,182,1) 100%);`
        case CATEGORIES.HEALTHY:
            return `linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(0,255,14,1) 100%);`
        case CATEGORIES.OVERWEIGHT:
            return `linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(255,134,0,1) 100%);`
        case CATEGORIES.OBESE:
            return `linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(194,18,18,1) 100%);`
        default:
            return `linear-gradient(90deg, rgba(9,9,121,1) 0%, rgba(0,212,255,1) 100%)`
    };
}
