import { Fragment, React } from "react";
import { useFormContext } from "../form-context/form-context.component";
import "./bmi-form.styles.jsx"
import {CustomInput, ErrorLabelHeight, ErrorLabelWeight, FormContainer } from "./bmi-form.styles"

const BMIForm = () => {
    const {height, weight, setHeight, setWeight} = useFormContext();

    const onHeightChange = (event) => {
        setHeight(event.target.value);
    }

    const onWeightChange = (event) => {
        setWeight(event.target.value);
    }

    return (
    <Fragment>
        {(height && (height <= 0 || height >= 3)) ? 
            (<ErrorLabelHeight className="error-label-height">Height has to be between 0m - 3m</ErrorLabelHeight>): <></>}
        {(weight && (weight <= 0 || weight >= 700)) ? 
            (<ErrorLabelWeight className="error-label-weight">Weight has to be between 0kg - 700kg</ErrorLabelWeight>): <></>}
        <FormContainer>
            <CustomInput 
                id='form-height' 
                autoComplete="off" 
                type="number" 
                required 
                step="0.01" 
                min="0.10" 
                max="3" 
                placeholder='height (m)' 
                value={height} 
                onChange={onHeightChange}>
            </CustomInput>
            <CustomInput 
                id='form-weight' 
                autoComplete="off" 
                type="number" 
                required 
                step="0.01" 
                min="2" 
                max="700" 
                placeholder='weight (kg)' 
                value={weight} 
                onChange={onWeightChange}>
            </CustomInput>
        </FormContainer>
    </Fragment>
    )
}

export default BMIForm;
