import React, {Fragment, useEffect, useState} from "react";
import {squared} from "../../utils/utils"
import { useFormContext } from "../form-context/form-context.component";
import "./bmi-display.styles.scss"


const BMIDisplay = () => {
    const [bmi, setBmi] = useState(0);
    const {height, weight} = useFormContext();

    useEffect(()=>{
        if(height && weight) {
            setBmi((weight/squared(height)).toFixed(2))
        }else {
            setBmi(0)
        }
    }, [height, weight])

    return (
        <Fragment>
            {(height && (height <= 0 || height >= 3)) ? 
                (<label className="error-label-height">Height has to be between 0m - 3m</label>): <></>}
            {(weight && (weight <= 0 || weight >= 700)) ? 
                (<label className="error-label-weight">Weight has to be between 0kg - 700kg</label>): <></>}
            <div className="bmi-container">
                <label id='bmi-value'>{bmi}</label>
            </div>
        </Fragment>
    )
}

export default BMIDisplay;
