import React from "react";
import { useSelector } from "react-redux";
import recomendations from "../../assets/recomendations";
import { selectBmi } from "../../redux/slices/bmiSlice";

const BMIRecomendation = ({ category }) => {
    const bmi = useSelector(selectBmi)

    return(
    <div>
        <h1>{category}</h1>
        <h2>BMI recomendations page - Your BMI: {bmi}</h2>
        {recomendations[category].map(paragraph => (
            <>
                <h3>{paragraph.subtitle}</h3>
                <p>{paragraph.text}</p>
            </>
        ))}
    </div>
    )
};

export default BMIRecomendation;
