import { Fragment, React } from "react";
import { useFormContext } from "../form-context/form-context.component";
import "./bmi-form.styles.scss"

const BMIForm = () => {
    const {height, weight, setHeight, setWeight} = useFormContext();

    const onHeightChange = (event) => {
        setHeight(event.target.value);
    }

    const onWeightChange = (event) => {
        setWeight(event.target.value);
    }

    return (
    <Fragment>
        {height && (height <= 0 || height >= 3) ? 
            (<label className="error-label-height">Height has to be between 0-3</label>): <></>}
        <form>
            <input id='form-height' autocomplete="off" type="number" required step="0.01" min="0.10" max="3" placeholder='height (m)' value={height} onChange={onHeightChange}></input>
            <input id='form-weight' autocomplete="off" type="number" required step="0.01" min="2" max="700" placeholder='weight (kg)' value={weight} onChange={onWeightChange}></input>
        </form>
    </Fragment>
    )
}

export default BMIForm;
