import React, {useEffect, useState} from "react";
import {squared} from "../../utils/utils"
import { useFormContext } from "../form-context/form-context.component";
import "./bmi-display.styles.scss"


const BMIDisplay = () => {
    const [bmi, setBmi] = useState(0);
    const {height, weight} = useFormContext();

    useEffect(()=>{
        if(height && weight) {
            setBmi(weight/squared(height))
        }else {
            setBmi(0)
        }
    }, [height, weight])

    return (
        <div className="bmi-container">
            <label id='bmi-value'>{bmi}</label>
        </div>
    )
}

export default BMIDisplay;
