import { Fragment, React } from "react";
import "./bmi-form.styles.jsx"
import {CustomInput, FormContainer } from "./bmi-form.styles"
import { useDispatch, useSelector } from "react-redux";
import { setHeight, setWeight, selectHeight, selectWeight, toggleIsHeightError, toggleIsWeightError } from '../../bmiSlice'

const BMIForm = () => {
    const height = useSelector(selectHeight)
    const weight = useSelector(selectWeight)
    const dispatch = useDispatch();

    const onHeightChange = (event) => {
        const newHeight = event.target.value
        dispatch(setHeight(newHeight));

        if(newHeight && (newHeight <= 0 || newHeight >= 3)){
            dispatch(toggleIsHeightError)
        }
    }

    const onWeightChange = (event) => {
        const newWeight = event.target.value;
        dispatch(setWeight(newWeight));

        if(newWeight && (newWeight <= 0 || newWeight >= 700)){
            dispatch(toggleIsWeightError)
        }
    }

    return (
    <Fragment>
        <FormContainer>
            <CustomInput 
                autoComplete="off" 
                type="number" 
                required 
                step="0.01" 
                min="0.10" 
                max="3" 
                placeholder='height (m)' 
                value={height} 
                onChange={onHeightChange}>
            </CustomInput>
            <CustomInput 
                autoComplete="off" 
                type="number" 
                required 
                step="0.01" 
                min="2" 
                max="700" 
                placeholder='weight (kg)' 
                value={weight} 
                onChange={onWeightChange}>
            </CustomInput>
        </FormContainer>
    </Fragment>
    )
}

export default BMIForm;
