import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    height: '',
    weight: '',
    bmi: 0,
    isError: false
}

export const bmiSlice = createSlice({
    name: 'bmi',
    initialState,
    reducers: {
        setHeight: (state, payload) => {
            state.height = payload.height;
        },
        setWeight: (state, payload) => {
            state.weight = payload.weight;
        }
    }
})
