import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    height: '',
    weight: '',
    bmi: 0,
    isHeightError: false,
    isWeightError: false
};

export const bmiSlice = createSlice({
    name: 'bmi',
    initialState,
    reducers: {
        setHeight: (state, action) => {
            state.bmi.height = action.payload;
        },
        setWeight: (state, action) => {
            state.bmi.weight = action.payload;
        },
        setBmi: (state, action) => {
            state.bmi.bmi = action.payload;
        },
        toggleIsHeightError: (state) => {
            state.bmi.isError = !state.isHeightError;
        }
    }
});

export const { 
    setHeight,
    setWeight,
    setBmi,
    toggleIsError
} = bmiSlice.actions


export const selectHeight = (state) => {
    return state.bmi.height;
}

export const selectWeight = (state) => {
    return state.bmi.weight;
}

export const selectBmi = (state) => {
    return state.bmi.bmi;
}

export const selectIsError = (state) => {
    return state.bmi.isError;
}


export default bmiSlice.reducer
