import styled from 'styled-components'

const handleTextColorByCategory = (category) => {
    switch(category){
        case CATEGORIES.UNDERWEIGHT:
            return `#9d0784`
        case CATEGORIES.HEALTHY:
            return `#6bde63`
        case CATEGORIES.OVERWEIGHT:
            return `#db8340`
        case CATEGORIES.OBESE:
            return `#ff0000`
        default:
            return `rgba(0,212,255,1)`
        };
    }

export const BMIContainer = styled.div`
    text-align: center;
    margin-top: 18vh;
`

export const BMIValue = styled.label`
    position: flex;
    color: ${({ category }) => handleTextColorByCategory(category)};
    font-size: 190px;
`
