import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    height: '',
    weight: '',
    bmi: 0,
    isError: false
};

export const bmiSlice = createSlice({
    name: 'bmi',
    initialState,
    reducers: {
        setHeight: (state, action) => {
            state.height = action.height;
        },
        setWeight: (state, payload) => {
            state.weight = payload.weight;
        },
        setBmi: (state, payload) => {
            state.bmi = payload.bmi;
        }
    }
});

export const selectHeight = (state) => {
    return state.height;
}

export const selectWeight = (state) => {
    return state.weight;
}

export const selectBmi = (state) => {
    return state.bmi;
}

export const { setHeight, setWeight, setBmi } = bmiSlice.actions

export default bmiSlice.reducer
